package JavaFirstProject;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int marks = 40;
		if(marks<35) {
			System.out.println("Marks = " + marks + " And the Result is Fail");
		}else {
			System.out.println("Marks = " + marks + " And the Result is Pass");
		}
	}

}
